"use client";
import clsx from "clsx";
import { EmojiStyle, Categories, Theme } from "emoji-picker-react";
import dynamic from "next/dynamic";
import { useCallback, useRef, useState } from "react";
import { useMediaQuery } from "react-responsive";

const EmojiPicker = dynamic(
  async () => (await import("emoji-picker-react")).default,
  {
    ssr: false,
  },
);

const dimensions = 512;

function blobToBase64(blob: Blob): Promise<string> {
  return new Promise((resolve, _) => {
    const reader = new FileReader();
    reader.onloadend = () => resolve(reader.result as string);
    reader.readAsDataURL(blob);
  });
}

function convertEmojiToDataToDataURL(emoji: string): string {
  const element = document.createElement("canvas");
  const ctx = element.getContext("2d")!;
  element.height = dimensions;
  element.width = dimensions;
  ctx.fillStyle = "rgb(24 24 27)";
  ctx.fillRect(0, 0, element.width, element.height);
  ctx.textAlign = `center`;
  ctx.textBaseline = `middle`;
  ctx.font = `${dimensions - 24}px serf`;
  ctx.fillText(emoji, 256, 256);
  return element.toDataURL("image/jpeg", 0.1);
}

const supportCategories = [
  Categories.ANIMALS_NATURE,
  Categories.FOOD_DRINK,
  Categories.TRAVEL_PLACES,
  Categories.ACTIVITIES,
  Categories.OBJECTS,
];

function getRandom<T>(arr: T[]): T {
  if (arr.length === 1) {
    return arr[0];
  }
  return arr[Math.floor(Math.random() * arr.length)];
}

export default function Home() {
  const [loading, setLoading] = useState(false);
  const emojiRef = useRef({ emoji: "üê±", name: "cat" });
  const isSmallScreen = useMediaQuery({
    query: "(max-width: 768px)",
  });

  const request = useCallback((emoji: string, name: string) => {
    setLoading(true);
    const paintingTypes = [
      "oil painting",
      "watercolor painting",
      "acrylic painting",
      "drawing",
      "printmaking",
    ];

    const artStyles = [
      "realism",
      "impressionism",
      "expressionism",
      "cubism",
      "surrealism",
      "baroque",
      "rococo",
      "fauvism",
    ];
    const prompt = `beautify ${getRandom(paintingTypes)}, ${getRandom(
      artStyles,
    )} style`;

    fetch("/api/run", {
      headers: {
        accept: "image/jpeg",
        "content-type": "application/json",
      },
      body: JSON.stringify({
        input_image: convertEmojiToDataToDataURL(emoji).replace(
          /^data:image\/(png|jpeg);base64,/,
          "",
        ),
        prompt: `cute ${name}, ${prompt}`,
        guidance_scale: 8,
        lcm_steps: 50,
        seed: Math.floor(Math.random() * 2159232),
        steps: 4,
        strength: 0.7,
        width: 512,
        height: 512,
      }),
      method: "POST",
    })
      .then((r) => r.blob())
      .then((d) => blobToBase64(d))
      .then(setImage)
      .catch(() => setLoading(false))
      .finally(() => setLoading(false));
  }, []);

  const [image, setImage] = useState(
    "data:image/jpeg;base64,",
  );
  return (
    <div className="min-h-screen flex flex-col gap-4 bg-zinc-950 items-center justify-center py-12">
      <div className="text-6xl text-zinc-100">üê± useEmoji </div>
      <div className="text-xl text-zinc-100">
        Turn emoji into amazing artwork via AI
      </div>
      <div className="flex items-center justify-center flex-col md:flex-row gap-4">
        <div className="flex-0 w-full md:w-80">
          <EmojiPicker
            width={"100%"}
            height={isSmallScreen ? 300 : 512}
            searchDisabled={isSmallScreen}
            onEmojiClick={(e) => {
              const emoji = e.emoji;
              const name = e.names[e.names.length - 1];
              emojiRef.current = { emoji, name };
              request(emoji, name);
            }}
            skinTonesDisabled
            emojiStyle={EmojiStyle.NATIVE}
            theme={Theme.DARK}
            categories={supportCategories.map((c) => ({
              name: c,
              category: c,
            }))}
          ></EmojiPicker>
        </div>
        <div className="flex-1">
          <div className="max-w-[100vw] h-[512px] w-[512px] rounded-lg overflow-hidden bg-zinc-900 relative">
            <img src={image} className="h-full w-full object-contain" />
            <div
              className={clsx("transition absolute inset-0", {
                "backdrop-blur-xl": loading,
              })}
            ></div>
            <div className="absolute right-2 bottom-2 flex gap-2">
              <button
                type="button"
                onClick={() =>
                  request(emojiRef.current.emoji, emojiRef.current.name)
                }
                className=" rounded bg-amber-600 px-2 py-1 text-sm font-semibold text-white shadow-sm hover:bg-amber-500 focus-visible:outline focus-visible:outline-2 focus-visible:outline-offset-2 focus-visible:outline-amber-600"
              >
                Switch art style
              </button>
              <a
                href={image}
                download
                type="button"
                className="rounded bg-amber-600 px-2 py-1 text-sm font-semibold text-white shadow-sm hover:bg-amber-500 focus-visible:outline focus-visible:outline-2 focus-visible:outline-offset-2 focus-visible:outline-amber-600"
              >
                Download
              </a>
            </div>
          </div>
        </div>
      </div>
    </div>
  );
}
